import React from 'react'
import {connect} from 'react-redux'
import { Link } from 'react-router-dom'
import { startSetIngredients } from '../../actions/receipeAction'

function IngredientsList(props){
    if(props.ingredient.length==0){
        props.dispatch(startSetIngredients())
    }
    return(
        <div>
            <h1>Categories List-{props.ingredient.length}</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>name</th>
                    </tr>
                </thead>
                <tbody>
                    {props.categories.map((ingredient,i)=>{
                        return(
                            <tr key={ingredient._id}>
                                <td>{i+1}</td>
                                <td><Link to={`/ingredients/${ingredient._id}`}>{ingredient.name}</Link></td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            {/* <Link to='/categories/new' className='btn btn-primary'>Add Categories</Link> */}
        </div>
    )
}

const mapStateToProps=(state)=>{
    return{
        ingredients:state.ingredients
    }
}

export default connect(mapStateToProps)(IngredientsList)
