const receipeInitialState=[]

const receipeReducer=(state=receipeInitialState,action)=>{
    switch(action.type){
        case 'ADD_INGREDIENTS':{
            return [...state,action.payload]
        }
        case 'SET_INGREDIENTS':{
            return [...action.payload]
        }
        case 'ADD_RECEIPES':{
            return [...state,action.payload]
        }
        case 'SET_RECEIPES':{
            return [...action.payload]
        }
        default:{
            return state
        }
    }
}

export default receipeReducer
