import React from 'react'
import {startRegister} from '../actions/register'
import {connect} from 'react-redux'

class Register extends React.Component{
    constructor(){
        super()
        this.state={
            username:'',
            email:'',
            password:''
        }
    }
    handleChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value
        })
    }
    handleSubmit=(e)=>{
        e.preventDefault()
        const formData={
            username:this.state.username,
            email:this.state.email,
            password:this.state.password
        }
        // console.log(formData)
        const redirect=()=>{
            return this.props.history.push('/users/login')
        }
        this.props.dispatch(startRegister(formData,redirect))
    }
    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className='form-group'>
                    <label>Username</label>
                    <input id='name' name='username' value={this.state. username} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>email</label>
                    <input id='email' name='email' value={this.state.email} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>password</label>
                    <input id='password' name='password' value={this.state.password} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <input type='submit' value='register' className='btn btn-danger'/>
                </form>
            </div>
        )
    }
}

export default connect()(Register)
