import React from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import { startSetReceipes } from '../../actions/receipeAction'

function ReceipesList(props){
    console.log(props,'props')
    if(props.receipes.length==0){
        props.dispatch(startSetReceipes())
    }
    return(
        <div>
            {/* <h1>Receipes List-{props.receipes.length}</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>name</th>
                        <th>description</th>
                    </tr>
                </thead>
                <tbody>
                    {props.receipes.map((receipe,i)=>{
                        return(
                            <tr key={receipe._id}>
                                <td>{i+1}</td>
                                <td><Link to={`/receipes/${receipe._id}`}>{receipe.name}</Link></td>
                                <td>{receipe.description}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table> */}
            <Link to='/receipes/new' className='btn btn-primary'>Add Receipes</Link>
        </div>
    )
}

const mapStateToProps=(state)=>{
    console.log(state)
    return{
        receipes:state.receipes
    }
}

export default connect(mapStateToProps)(ReceipesList)
