import axios from 'axios'

export const setIngredients=(ingredients)=>{
    return {type:'SET_INGREDIENTS',payload:ingredients}
}

export const startSetIngredients=()=>{
    return(dispatch)=>{
        axios.get('http://localhost:3040/ingredients')
        .then((response)=>{
            const ingredients=response.data
            console.log(ingredients)
            dispatch(setIngredients(ingredients))
        })
    }
}

export const addIngredients=(ingredients)=>{
    return {type:'ADD_INGREDIENTS', payload:ingredients}
}

export const startAddIngredients=(formData,redirect)=>{
    return(dispatch)=>{
        axios.post('http://localhost:3040/ingredients',formData)
        .then((response)=>{
            const ingredients=response.data
            dispatch(addIngredients(ingredients))
            redirect()
        })
    }
}


export const setReceipes=(receipes)=>{
    return {type:'SET_RECEIPES',payload:receipes}
}

export const startSetReceipes=()=>{
    return(dispatch)=>{
        axios.get('http://localhost:3040/receipes')
        .then((response)=>{
            const receipes=response.data
            dispatch(setReceipes(receipes))
        })
    }
}

export const addReceipes=(receipes)=>{
    return {type:'ADD_RECEIPES', payload:receipes}
}

export const startAddReceipes=(formData,redirect)=>{
    return(dispatch)=>{
        axios.post('http://localhost:3040/receipes',formData)
        .then((response)=>{
            const receipes=response.data
            dispatch(addReceipes(receipes))
            redirect()
        })
    }
}
