import React from 'react'
import './signup.css'
import { Link } from 'react-router-dom'

const formData={
    username:'',
    email:'',
    password:'',
    confirmPassword:'',
    nameError:'',
    emailError:'',
    passwordError:''
}


class SignUp extends React.Component{
   
    constructor(){
        super()
       this.state=formData
    }
    handleChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value
        })
    }
    // validate=()=>{
    //     let nameError='';
    //     let emailError='';
    //     let passwordError='';
    
    //     if(!this.state.name){
    //         nameError='name cannot be blank'
    //     }
    
    //     if(!this.state.email.includes('@')){
    //       emailError='invalid email'
    //     }
        
    //     if(this.state.password.length<6){
    //       passwordError='password should contain atleast 6 letters'
    //     }
    //     if(emailError || nameError || passwordError ){
    //         this.setState({emailError,nameError,passwordError})
    //         return false
    //       }
    //   return true
    // }
    
    handleSubmit=(e)=>{
        e.preventDefault()
        // const isValid=this.validate()
        // if(isValid){
        //   console.log(this.state)
        //   //clear the form
        //   this.setState(formData)
        // }    
        const formData={
            username:this.state.username,
            email:this.state.email,
            password:this.state.password,
            confirmPassword:this.state.confirmPassword
        }
        console.log(formData)
    }
    render(){
    return(
        <div>
            <form className="signup-container" onSubmit={this.handleSubmit} >
                <div className='row px-4 vh-100'>
                    <div className='col-md-5 mx-auto align-self-center'>
                {/* username */}
                <div className='form-group input-group'>
                    <div className='input-group-prepend'>
                        <span className='input-group-text'>
                            <i className='fa fa-user'></i>
                        </span>
                    </div>
                    <input 
                    name='username'
                    className='form-control'
                    placeholder='Username'
                    type='text'
                    value={this.state.username}
                    onChange={this.handleChange}
                    />
                </div>
                {/* <div className="alert alert-danger" role="alert">{this.state.nameError}</div> */}
                {/* mail */}
                <div className='form-group input-group'>
                    <div className='input-group-prepend'>
                        <span className='input-group-text'>
                            <i className='fa fa-envelope'></i>
                        </span>
                    </div>
                    <input 
                    name='email'
                    className='form-control'
                    placeholder='Email address'
                    type='email'
                    value={this.state.email}
                    onChange={this.handleChange}
                    />
                </div>
                {/* <div style={{fontSize:12,color:'red'}}>{this.state.emailError}</div> */}
                {/* create password */}
                <div className='form-group input-group'>
                    <div className='input-group-prepend'>
                        <span className='input-group-text'>
                            <i className='fa fa-lock'></i>
                        </span>
                    </div>
                    <input 
                    name='password'
                    className='form-control'
                    placeholder='Create Password'
                    type='password'
                    value={this.state.password}
                    onChange={this.handleChange}
                    />
                </div>
                <div className='form-group input-group'>
                    <div className='input-group-prepend'>
                        <span className='input-group-text'>
                            <i className='fa fa-lock'></i>
                        </span>
                    </div>
                    <input 
                    name='confirmPassword'
                    className='form-control'
                    placeholder='Confirm Password'
                    type='password'
                    value={this.state.confirmPassword}
                    onChange={this.handleChange}
                    />
                </div>
                {/* <div style={{fontSize:12,color:'red'}}>{this.state.passwordError}</div> */}
                {/* signup button */}
                <div className='form-group'>
                    <button type='submit' className='btn btn-primary btn-block'>
                        Create Account
                    </button>
                </div>
                {/* already hve an account */}
                <p className='text-center text-white'>
                    Have an account?<Link to='/signin'>Log in</Link>
                </p>
                </div>
                </div>
            </form>
        </div>
    )
    }
}

export default SignUp
