import React from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'

class ReceipeForm extends React.Component{
    constructor(){
        super()
        this.state={
            name:'',
            description:'',
            ingredients:''
        }
        handleChange=(e)=>{
            this.setState({
                [e.target.name]:e.target.value
            })
      }
      handleSubmit=(e)=>{
          // const id=this.props.notes._id
          e.preventDefault()
          const formData={
              title:this.state.body,
              body:this.state.title,
              category:this.state.category
          }
         this.props.handleSubmit(formData)
      }
    }
    render(){
        return(
            <div>
            <form onSubmit={this.handleSubmit}>
                    <div className='form-group'>
                    <label>Name</label>
                    <input type='text' name='name' value={this.state.name} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>Desciption</label>
                    <input type='text' name='description' value={this.state.description} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <input type='submit' value='Get Full Receipe' className='btn btn-secondary'/>
                </form>
             </div>
        )
    }
}

export default connect()(ReceipeForm)
