import React from 'react'
import {connect} from 'react-redux'
import {startLogin} from '../actions/register'
import {Redirect} from 'react-router-dom'

class Login extends React.Component{
    constructor(){
        super()
        this.state={
            email:'',
            password:''
        }
    }
    handleChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value
        })
    }
    handleSubmit=(e)=>{
        e.preventDefault()
        const formData={
            email:this.state.email,
            password:this.state.password
        }
        const redirect=()=>{
             return this.props.history.push('/receipes')
        }
        this.props.dispatch(startLogin(formData,redirect))
     
    }
    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className='form-group'>
                    <label>Email</label>
                    <input id='email' name='email' value={this.state.email} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>Password</label>
                    <input id='password' type='password' name='password' value={this.state.password} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <input type='submit' value='login' className='btn btn-danger'/>
                </form>
            </div>
        )
    }
}

export default connect()(Login)
