import React from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'

class ReceipeForm extends React.Component{
    constructor(){
        super()
        this.state={
            name:'',
            description:'',
            ingredients:''
        }
    }
    render(){
        return(
            <div>
            <form onSubmit={this.handleSubmit}>
                    <div className='form-group'>
                    <label>title</label>
                    <input type='text' name='title' value={this.state.title} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>body</label>
                    <input type='text' name='body' value={this.state.body} onChange={this.handleChange} className='form-control'/>
                    </div>
                    <div className='form-group'>
                    <label>category</label>
                    <select id="category" value={this.state.category} className="form-control" name="category" onChange={this.handleChange} className='form-control'>
                            <option key="one">select</option>
                            
                            {
                                this.props.categories ? this.props.categories.map(depart=>{
                                    return (<option value={depart._id} key={depart._id}>{depart.name}</option>)
                                }) : 'loading'
                            }

                        </select>
                        </div>
                    <input type='submit' value='Add Notes' className='btn btn-secondary'/>
                </form>
             </div>
        )
    }
}

export default ReceipeForm
